import { Fragment, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

export default function FarmerBillDetails() {
    const [billdetails, setBillDetails] = useState(null);
    const { id } = useParams();

    useEffect(() => {
        fetch(`${process.env.REACT_APP_API_URI}/farmers${id}`)
            .then(res => res.json())
            .then(res => setBillDetails(res));
    }, [id]);

    // if (!billdetails) {
    //     return <div>Loading...</div>;
    // }

    let totalAmount = 0;
    let totalQuantity = 0;

    const rows = billdetails.quantity.map((qty, i) => {
        const rate = billdetails.rate[i];
        const rowTotal = rate * qty;
        totalAmount += rowTotal;
        totalQuantity += qty;

        return (
            <tr key={i}>
                <td>{rate}</td>
                <td>{qty}</td>
                <td>{rowTotal}</td>
            </tr>
        );
    });

    const commission = Math.floor(totalAmount / 10);
    const labourWages = 5 * totalQuantity;
    const advance = 100;
    const remaining = totalAmount - (commission + labourWages + advance);

    return (billdetails &&
        <Fragment>
            <style>{`
                body {
                    font-family: 'Segoe UI', sans-serif;
                    background-color: #f1f8e9;
                    margin: 0;
                    padding: 2rem;
                }
                h1 {
                    text-align: center;
                    color: #33691e;
                    margin-bottom: 2rem;
                }
                .bill-section {
                    max-width: 800px;
                    background: white;
                    padding: 2rem;
                    margin: auto;
                    border-radius: 10px;
                    box-shadow: 0 8px 16px rgba(0,0,0,0.1);
                }
                .info {
                    margin-bottom: 1.5rem;
                }
                .info div {
                    margin: 0.4rem 0;
                    font-weight: bold;
                }
                table {
                    width: 100%;
                    border-collapse: collapse;
                    margin-top: 1rem;
                }
                th, td {
                    padding: 0.8rem;
                    text-align: center;
                    border: 1px solid #ccc;
                }
                .totals {
                    margin-top: 1.5rem;
                }
                .totals div {
                    margin: 0.5rem 0;
                    font-weight: bold;
                }
                .highlight {
                    color: #d84315;
                    font-size: 1.2rem;
                }
            `}</style>

            <h1>Farmer Bill Details</h1>

            <div className="bill-section">
                <div className="info">
                    <div>Name: <span>{billdetails.name || 'Kumar'}</span></div>
                    <div>Place: <span>{billdetails.place || 'Salem'}</span></div>
                </div>

                <table>
                    <thead>
                        <tr>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Total</th>
                        </tr>
                    </thead>
                    <tbody>{rows}</tbody>
                </table>

                <div className="totals">
                    <div>Total Quantity: {totalQuantity}</div>
                    <div>Total Amount: ₹{totalAmount}</div>
                    <div>Commission (10%): ₹{commission}</div>
                    <div>Labour Wages (₹5 x {totalQuantity}): ₹{labourWages}</div>
                    <div>Advance Paid: ₹{advance}</div>
                    <div className="highlight">Remaining Amount: ₹{remaining}</div>
                </div>
            </div>
        </Fragment>
    );
}
