import {Fragment} from 'react'
import { Link } from 'react-router-dom'
import { Helmet } from 'react-helmet';

export default function OwnerDashboard(){
    return <Fragment>
                <style>{`
                            body {
                                font-family: 'Segoe UI', sans-serif;
                                background-color: #f1f8e9;
                                margin: 0;
                                padding: 2rem;
                            }

                            h1 {
                                text-align: center;
                                color: #33691e;
                                margin-bottom: 2rem;
                            }

                            .dashboard {
                                display: flex;
                                flex-direction: column;
                                gap: 1.5rem;
                                width: 100%;
                                max-width: 1000px;
                                margin: auto;
                            }

                            .card {
                                background: white;
                                padding: 2rem;
                                border-radius: 10px;
                                box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
                                transition: transform 0.2s ease;
                                width: 100%;
                            }

                            .card:hover {
                                transform: translateY(-5px);
                            }

                            .card h2 {
                                margin-top: 0;
                                color: #2e7d32;
                            }

                            .card p {
                                font-size: 1.1rem;
                                margin: 0.5rem 0;
                            }
                            a{
                                text-decoration: none;
                                color: inherit;
                            }
            `}</style>
                  <header>
                    Owner Dashboard
                </header>

                <section class="dashboard">
                    <a href="shopDetails.html">
                    <div class="card">
                        <h2>Shop Details</h2>
                        <p>To the Shop Details like total transactions, profit etc..</p>
                    </div>
                    </a>

                    <a href="sellerdetails.html">
                    <div class="card">
                        <h2>Seller Details</h2>
                        <p>To see the Seller Details</p>
                    </div>
                    </a>

                    <a href="farmerBillPayementPage.html">
                    <div class="card">
                        <h2>Farmer Details</h2>
                        <p>To see the Farmer Details</p>
                    </div>
                    </a>

                    <a href="sellerCredit.html">
                    <div class="card">
                        <h2>Seller Credit</h2>
                        <p>To update the seller Credit Amount</p>
                    </div>
                    </a>
                </section>
            </Fragment>
}
