import { useState } from "react"
import { Link } from "react-router-dom"

export default function SellerCreditCard({seller}){
    const [amount,setAmount]=useState(0);
    function handleChange(e){
        var value = Ine.target.value;
        setAmount(value);
    }
    
    return <>
                <Link to={"/sellerPaymentDetail/"+seller._id} className="link"> 
                    <div class="seller-card">
                        <div class="seller-name">{seller.name.toUpperCase()}</div>
                        <div class="seller-place">{seller.place}</div>
                        <div class="credit-display" id="credit-ravi">Current Credit: â‚¹0</div>
                        <div class="credit-section">
                        <input type="number" id="input-ravi" placeholder="Enter new credit" onChange={handleChange} />
                        <button>Update</button>
                        </div>
                    </div>
                </Link>
            </>
}
