import React, { Fragment, useState } from "react";

function FarmerBilling() {
  const [rows, setRows] = useState([{ price: "", quantity: "" }]);
  const [totalQuantity, setTotalQuantity] = useState(0);
  const [advance, setAdvance] = useState(0);
  const [billDetails, setBillDetails] = useState(null);
  const [name,setName] = useState("");
  const [place,setPlace] = useState("");
  const [date,setDate] = useState("");

  const addRow = () => {
    setRows([...rows, { price: "", quantity: "" }]);
  };

  const handleInputChange = (index, field, value) => {
    const updatedRows = [...rows];
    updatedRows[index][field] = value;
    setRows(updatedRows);
  };

  const generateBill = () => {
    let totalAmount = 0;
    let labourWages = 5 * totalQuantity;

    const calculatedRows = rows.map(({ price, quantity }) => {
      const priceNum = parseFloat(price) || 0;
      const quantityNum = parseFloat(quantity) || 0;
      const total = priceNum * quantityNum;
      totalAmount += total;
      return { price: priceNum, quantity: quantityNum, total };
    });

    const commission = Math.floor(totalAmount / 10);
    const remaining = totalAmount - (commission + labourWages + advance);

    setBillDetails({
      rows: calculatedRows,
      totalAmount,
      commission,
      labourWages,
      remaining,
    });
  };
  function updateDatabase(){

  }
  return (
    <Fragment>
        <style>{`
            body {
              font-family: 'Segoe UI', sans-serif;
              background-color: #f1f8e9;
              margin: 0;
              padding: 2rem;
            }

            h1 {
              text-align: center;
              color: #33691e;
              margin-bottom: 2rem;
            }
            
            .form-section,
            .bill-section {
              max-width: 800px;
              background: white;
              padding: 2rem;
              margin: auto;
              margin-bottom: 2rem;
              border-radius: 10px;
              box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
            }

            label {
              display: block;
              margin: 1rem 0 0.3rem;
              font-weight: bold;
            }

            input[type="text"],
            input[type="number"] {
              width: 100%;
              padding: 0.6rem;
              border-radius: 5px;
              border: 1px solid #ccc;
            }

            .price-rows {
              margin-top: 1rem;
            }

            button {
              padding: 0.8rem 1.5rem;
              font-size: 1rem;
              border: none;
              background-color: #388e3c;
              color: white;
              border-radius: 5px;
              cursor: pointer;
              margin-top: 1rem;
            }

            button:hover {
              background-color: #2e7d32;
            }

            table {
              width: 100%;
              border-collapse: collapse;
              margin-top: 1.5rem;
            }

            table,
            th,
            td {
              border: 1px solid #ccc;
            }

            th,
            td {
              padding: 0.8rem;
              text-align: center;
            }

            .totals {
              margin-top: 1.5rem;
            }

            .totals div {
              margin: 0.5rem 0;
              font-weight: bold;
            }

            .highlight {
              color: #d84315;
              font-size: 1.2rem;
            }

      `}</style>
    <div>
      <h1>Farmer Billing</h1>

      <div className="form-section">
        <label>Farmer Name:</label>
        <input type="text" placeholder="Enter Farmer Name" />

        <label>Place:</label>
        <input type="text" placeholder="Enter Place" />
        <input type="date"/>

        <label>Total Quantity:</label>
        <input
          type="number"
          placeholder="Enter Total Quantity"
          onChange={(e) => setTotalQuantity(parseFloat(e.target.value) || 0)}
        />

        <div className="price-rows">
          {rows.map((row, index) => (
            <div key={index} style={{ display: "flex", gap: "1rem", marginBottom: "1rem" }}>
              <input
                type="number"
                placeholder="Price (Amount)"
                value={row.price}
                onChange={(e) => handleInputChange(index, "price", e.target.value)}
              />
              <input
                type="number"
                placeholder="Quantity"
                value={row.quantity}
                onChange={(e) => handleInputChange(index, "quantity", e.target.value)}
              />
            </div>
          ))}
        </div>

        <button onClick={addRow}>+ Add Price & Quantity</button>

        <label>Advance Amount:</label>
        <input
          type="number"
          placeholder="Enter Advance Amount"
          onChange={(e) => setAdvance(parseFloat(e.target.value) || 0)}
        />

        <button onClick={generateBill}>Generate Bill</button>
      </div>

      {billDetails && (
        <div className="bill-section">
          <h2>Billing Details</h2>
          <table>
            <thead>
              <tr>
                <th>Price</th>
                <th>Quantity</th>
                <th>Total</th>
              </tr>
            </thead>
            <tbody>
              {billDetails.rows.map((row, index) => (
                <tr key={index}>
                  <td>{row.price}</td>
                  <td>{row.quantity}</td>
                  <td>{row.total}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="totals">
            <div>Total Amount: ₹{billDetails.totalAmount}</div>
            <div>Commission (10%): ₹{billDetails.commission}</div>
            <div>
              Labour Wages (₹5 x {totalQuantity}): ₹{billDetails.labourWages}
            </div>
            <div>Advance Paid: ₹{advance}</div>
            <div className="highlight">
              Remaining Amount: ₹{billDetails.remaining}
            </div>
          </div>
          <button onClick={updateDatabase} style={{ marginTop: "20px", backgroundColor: "#1e88e5" }}>
            Update Database
          </button>
        </div>
      )}
    </div>
    </Fragment>
  );
}

export default FarmerBilling;
