import { useState } from "react";
import { Link } from "react-router-dom";

export default function SellerCreditCard({ seller }) {
    const [amount, setAmount] = useState(0);
    const [notification, setNotification] = useState('');

    function handleChange(e) {
        setAmount(parseInt(e.target.value));
    }
    function updateAmount(){
        fetch(process.env.REACT_APP_API_URI+'/sellers/pay/'+sel ,
            { 
                method: "PUT" ,
                body: JSON.stringify({
                    amount
                }), 
                headers:{
                    "content-Type":"application/json; charset:UTF-8"
                }
            }
        )
        .then(res => {
            if (!res.ok) throw new Error('Failed to update');
            return res.json();
        })
        .then(data => {
            setNotification("Amount updated successfully!");
            setTimeout(() => setNotification(''), 3000);
        })
        .catch(err => {
            setNotification("Failed to update.");
            setTimeout(() => setNotification(''), 3000);
        });
    }

    return (
        <>
        {notification && (
            <div className="top-notification">
                {notification}
            </div>
        )}
        <div className="seller-card">
            <div>
                <Link to={"/sellerPaymentDetail/"+seller._id } className='link'>
                    {seller.name.toUpperCase()}
                </Link>
            </div>
            <div>
                <Link to={"/sellerPaymentDetail/"+seller._id} className='link'>
                    {seller.place}
                </Link>
            </div>
            <div className="credit-display" id="credit-ravi">
                Current Credit: â‚¹{amount}
                {typeof(amount)}
            </div>
            <div className="credit-section">
                <input
                    type="number"
                    id="input-ravi"
                    placeholder="Enter new credit"
                    onChange={handleChange}
                />
                <button onClick={updateAmount}>Update</button>
            </div>
        </div>
        </>
    );
}
