import {Fragment} from 'react'
import { Link } from 'react-router-dom'
import { Helmet } from 'react-helmet';

export default function OwnerDashboard(){
    return <Fragment>
                <Helmet>
                    <link rel="stylesheet" href="/css/ownerdashboard.css" />
                </Helmet>
                <style>{`
                * {
                        margin: 0;
                        padding: 0;
                        box-sizing: border-box;
                        font-family: 'Segoe UI', sans-serif;
                    }

                    body {
                        background-color: #f3f7f0;
                        color: #333;
                        min-height: 100vh;
                        display: flex;
                        flex-direction: column;
                    }

                    header {
                        background-color: #4CAF50;
                        color: white;
                        padding: 1rem;
                        text-align: center;
                        font-size: 1.8rem;
                        font-weight: bold;
                    }

                    .dashboard {
                        flex: 1;
                        display: grid;
                        grid-template-columns: repeat(2, 1fr);
                        grid-template-rows: repeat(2, 1fr);
                        gap: 2rem;
                        padding: 2rem;
                        max-width: 1200px;
                        margin: auto;
                        height: 100%;
                    }

                    .card {
                        background-color: white;
                        border-radius: 15px;
                        box-shadow: 0 4px 8px rgba(0,0,0,0.1);
                        padding: 3rem 2rem;
                        text-align: center;
                        transition: transform 0.3s, box-shadow 0.3s;
                        cursor: pointer;
                        display: flex;
                        flex-direction: column;
                        justify-content: center;
                    }

                    .card:hover {
                        transform: translateY(-5px);
                        box-shadow: 0 8px 16px rgba(0,0,0,0.15);
                    }

                    .card h2 {
                        margin-bottom: 1rem;
                        color: #2e7d32;
                        font-size: 2rem;
                    }

                    .card p {
                        font-size: 1.1rem;
                        color: #666;
                    }

                    a {
                        text-decoration: none;
                        color: inherit;
                    }

                    @media (max-width: 768px) {
                        .dashboard {
                        grid-template-columns: 1fr;
                        grid-template-rows: auto;
                        }
                    }
            `}</style>
                <h1>Owner Dashboard</h1>

                <div className="dashboard">
                <a href="shopDetails.html">
                    <div className="card">
                        <h2>Shop Details</h2>
                        <p>To the Shop Details like total transactions, profit etc..
                        </p>
                    </div>
                </a>
                <a href="sellerdetails.html">
                    <div className="card">
                        <h2>Seller Details</h2>
                        <p>To see the Seller Details</p>
                    </div>
                </a>
                <a href="farmerBillPayementPage.html">
                    <div className="card">
                        <h2>Farmer Details</h2>
                        <p>To see the Farmer Details</p>
                    </div>
                </a>
                
                <a href="sellerCredit.html">
                    <div className="card">
                        <h2>Seller Credit</h2>
                        <p>To update the seller Credit Amount</p>
                    </div>
                </a>
                
                </div>
            </Fragment>
}
